import React, {useEffect, useReducer, useState} from 'react';
import {Obstacle, Vec2} from '../utility';
import Grid from './Grid';

// type Action = {
//     type: string;
// };

// const reducer = (state: Vec2, action: Action) => {
//     switch (action.type) {
//     }
// };

type State = {
    position: Vec2;
    obstacles: Obstacle[];
};

type Action = {
    type: 'moveUp' | 'moveDown';
};

type Reducer<State, Action> = (prevState: State, action: Action) => State;

const reducer = (state: State, action: Action): State => {
    const newObstacles = state.obstacles.map((obstacle) => {
        obstacle.col -= 1;
        return obstacle;
    });
    switch (action.type) {
        case 'moveUp':
            let newPosition = new Vec2(
                state.position.row - 1,
                state.position.col
            );
            return {
                ...state,
                newPosition,
                newObstacles
            };
        case 'moveDown':
            newPosition = new Vec2(state.position.row + 1, state.position.col);
            return {
                ...state,
                newPosition,
                newObstacles
            };
        default:
            return state;
    }
};

const Game: React.FC = () => {
    const initialObstacles: Obstacle[] = [
        {col: 6, height: 3, side: 'bottom'},
        {col: 8, height: 4, side: 'top'},
        {col: 8, height: 2, side: 'bottom'}
    ];
    const initialState: State = {
        position: new Vec2(3, 3),
        obstacles: initialObstacles
    };
    //const [position, setPosition] = useState<Vec2>(new Vec2(3, 3));
    //const [obstacles, setObstacles] = useState<Obstacle[]>(initialObstacles);
    const [state, dispatch] = useReducer(reducer, initialState);
    const moveHandler = (event: KeyboardEvent) => {
        console.log('position');
        if (event.key == 'ArrowUp') {
            dispatch({type: 'moveUp'});
        } else if (event.key == 'ArrowDown') {
            dispatch({type: 'moveDow'});
        }
    };
    useEffect(() => {
        document.addEventListener('keydown', moveHandler);
        return () => document.removeEventListener('keydown', moveHandler);
    });
    return <Grid position={position} obstacles={obstacles} />;
};

export default Game;
