import React, {useEffect, useReducer, useState} from 'react';
import {Obstacle, Vec2} from '../utility';
import Grid from './Grid';

// type Action = {
//     type: string;
// };

// const reducer = (state: Vec2, action: Action) => {
//     switch (action.type) {
//     }
// };

const Game: React.FC = () => {
    const initialObstacles: Obstacle[] = [
        {col: 6, height: 3, side: 'bottom'},
        {col: 8, height: 4, side: 'top'}
    ];
    const [position, setPosition] = useState<Vec2>(new Vec2(3, 3));
    const [obstacles, setObstacles] = useState<Obstacle[]>(initialObstacles);
    // const [position, dispatch] = useReducer(reducer, new Vec2(3, 3));
    const moveHandler = (event: KeyboardEvent) => {
        console.log(position);
        if (event.key == 'ArrowUp') {
            setPosition(new Vec2(position.row - 1, position.col));
        } else if (event.key == 'ArrowDown') {
            setPosition(new Vec2(position.row + 1, position.col));
        }
        console.log(position);
        if (event.key == 'ArrowUp' || event.key == 'ArrowDown') {
            setObstacles(
                obstacles.map((obstacle) => {
                    obstacle.col -= 1;
                    return obstacle;
                })
            );
        }
        console.log(obstacles);
    };
    useEffect(() => {
        document.addEventListener('keydown', moveHandler);
        return () => document.removeEventListener('keydown', moveHandler);
    });
    return <Grid position={position} obstacles={obstacles} />;
};

export default Game;
